INSERT INTO `informasi_publik` (`id`, `category_id`, `judul`, `slug`, `konten`, `file_path`, `file_nama`, `file_tipe`, `thumbnail`, `tanggal_publikasi`, `hits`, `is_active`, `created_at`, `updated_at`) VALUES
(1, 1, 'Laporan Kinerja Instansi Pemerintah Tahun 2024', 'laporan-kinerja-instansi-pemerintah-tahun-2024', '<div class=\"content\">\r\n<p>Laporan Kinerja Instansi Pemerintah adalah dokumen resmi yang menyajikan informasi mengenai pencapaian kinerja suatu instansi dalam satu tahun anggaran sebagai bentuk pertanggungjawaban kepada publik. LKjIP berfungsi sebagai alat evaluasi dan perbaikan berkelanjutan, serta menjadi dasar dalam pengambilan kebijakan untuk meningkatkan efektivitas dan transparansi tata kelola pemerintahan.</p>\r\n</div>', 'informasi_publik/dokumen/GBvxghgONowW5TR7DVzK0lEDweUTxUgqGVcWIBVi.pdf', '[2024] LAKIP DESDM.pdf', 'application/pdf', NULL, '2025-06-23 17:00:00', 3, 1, '2025-07-24 05:00:56', '2025-08-03 08:19:47'),
(3, 2, 'Peringatan Dini Potensi Longsor di Area Pertambangan XYZ', 'peringatan-dini-potensi-longsor-di-area-pertambangan-xyz', '<p>Masyarakat diimbau untuk meningkatkan kewaspadaan terkait potensi longsor di area pertambangan XYZ menyusul intensitas hujan yang tinggi.</p><p>Dinas ESDM bersama pihak terkait sedang melakukan pemantauan intensif dan telah menyiapkan langkah-langkah mitigasi.</p>', NULL, NULL, NULL, NULL, '2025-07-24 00:00:56', 2, 1, '2025-07-24 05:00:56', '2025-07-28 13:11:07'),
(4, 3, 'Daftar IUP Mineral Bukan Logam dan Batuan di Sumsel', 'daftar-iup-mineral-bukan-logam-dan-batuan-di-sumsel', '<p>Daftar Izin Usaha Pertambangan (IUP) untuk komoditas mineral bukan logam dan batuan di wilayah Provinsi Sumatera Selatan.</p><p>Dokumen ini diperbarui secara berkala dan dapat diakses setiap saat oleh masyarakat.</p>', 'public/informasi_publik/Daftar-IUP-Non-Logam-Sumsel.xlsx', 'Daftar-IUP-Non-Logam-Sumsel.xlsx', 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet', NULL, '2025-07-14 05:00:56', 1, 1, '2025-07-24 05:00:56', '2025-07-28 13:10:49'),
(5, 3, 'Prosedur Permohonan Informasi Publik DESDM Sumsel', 'prosedur-permohonan-informasi-publik-desdm-sumsel', '<p>Panduan lengkap mengenai tata cara dan persyaratan untuk mengajukan permohonan informasi publik kepada Dinas ESDM Sumatera Selatan.</p>', 'public/informasi_publik/Prosedur-Permohonan-Informasi-Publik.pdf', 'Prosedur-Permohonan-Informasi-Publik.pdf', 'application/pdf', NULL, '2025-07-04 05:00:56', 0, 1, '2025-07-24 05:00:56', '2025-07-24 05:00:56'),
(6, 1, 'Rencana Strategis 2024-2026', 'rencana-strategis-2024-2026', '<div class=\"content\">\r\n<p>RENSTRA ini disusun dalam rangka mendukung program kerja Pemerintah Provinsi Sumatera Selatan sesuai dengan tugas pokok dan fungsi Dinas Energi dan Sumber Daya Mineral Provinsi Sumatera Selatan sekaligus sebagai acuan dan pedoman guna menyusun kerangka kebijakan, sasaran dan tujuan pembangunan sektor energi dan sumber daya mineral di Provinsi Sumatera Selatan dalam kurun waktu tahun 2024-2026.</p>\r\n</div>', 'informasi_publik/dokumen/XJaTmiSZcVMINC4Blg4eRCShRlVC09PAsv3nOVdd.pdf', 'RENSTRA_2024-2026_DESDM_fix.pdf', 'application/pdf', NULL, '2025-07-28 13:05:09', 2, 1, '2025-07-28 13:05:09', '2025-07-28 17:00:49'),
(7, 1, 'Perjanjian Kinerja 2025', 'perjanjian-kinerja-2025', '<div class=\"content\">\r\n<p>Perjanjian Kinerja adalah dokumen kesepakatan antara pimpinan instansi dan pejabat yang bertanggung jawab atas pencapaian target kinerja dalam satu tahun anggaran. Dokumen ini berfungsi sebagai komitmen kerja yang mengikat, guna memastikan pelaksanaan tugas dan tanggung jawab sesuai dengan perencanaan yang telah ditetapkan, serta menjadi dasar dalam evaluasi dan pertanggungjawaban kinerja.</p>\r\n</div>', 'informasi_publik/dokumen/vkV3jEm3VuLerrJRp01uI9rrwYgqGeGPOhCLijTR.pdf', 'PK KEPALA DINAS DESDM 2025.pdf', 'application/pdf', NULL, '2025-07-28 13:08:44', 1, 1, '2025-07-28 13:08:44', '2025-07-28 13:08:50');

